import React  from 'react';
import Title from './Title';
import Ecran from './Ecran';
import Resultat from './Resultat';
import Buttons from './Buttons';
import '../styles/first-component.css';

class Display extends React.Component{
	constructor(){
		super()
		this.state = {
			val:'',
			firstNum:0,
			secondNum:0,
			total:''
		}
	}

	calcul = (signe, num1 , num2) => {
		if(signe === 'x'){return num1 * num2;}
		if(signe === '%'){return num1 / num2;}
		if(signe === '+'){return num1 + num2;}
		if(signe === '-'){return num1 - num2;}
	}

	buttonClicked = (e) => {
      	//alert(e.target.value);
      	if(e.target.value === 'clear'){
      		this.setState({ val: ''});
      	}else if(e.target.value === '+'){
			this.setState({ firstNum: this.state.val});
      		alert(this.state.firstNum);
      		this.setState({ val: ''});
      		alert(this.state.firstNum);
      	}else if(e.target.value === '='){

      	}else{
      		this.setState({ val : this.state.val + e.target.value});
      	}

  	}

	render(){


		return(
			<div className='root'>
				<Title />
				<Ecran typed={this.state.val}/>
				<Resultat typed={this.state.total}/>
				<Buttons click={this.buttonClicked}/>
			</div>

		);
	}
}

export default Display;
