import React , {Component} from 'react';
import './App.css';
import CardList from './CardList';
import {Names} from './Data';
import Searchbox from './Searchbox';




class App extends Component{
  constructor() {
      super()
      this.state = {
        items:0,
        total:0,
        data : Names,
        searchfield: ''
      }
  }

  onSearchChange = (event) => {

      this.setState({ searchfield: event.target.value });
  }

  onClicked = () => {
      //alert(e.target);
      this.setState({items : this.state.items + 1});
      //this.setState({total : this.state.total + this.state.data[1].price});
      this.state.data.map((name, i) => (this.setState({total : this.state.total + this.state.data[i].price})))
  }

  render() {

     const filteredRobots = this.state.data.filter(Name => {

          return Name.name.toLowerCase().includes(this.state.searchfield.toLowerCase());

      })

      return (
          <div className='page'>
            <h1>RoboFriends</h1>
            <h2>Panier : {this.state.items} items</h2>
            <h2>Total : {this.state.total}$</h2>
            <Searchbox  searchchange = {this.onSearchChange} />
            <CardList click={this.onClicked} data={filteredRobots}/>
          </div>
      );
  }

}


export default App;
