      	}else{
      		this.setState({ val : this.state.val + e.target.value});
      		this.setState({ stocked: this.state.val });
      	}

  	}


		return(
			<div className='root'>
				<Title />
				<Ecran typed={val}/>
				<Resultat typed={total}/>
				<Buttons click={buttonClicked}/>
			</div>

		);
}

export default Display;
